import { AuthComponent } from './pages/auth/auth.component'; // <-- ADD THIS IMPORT

const routes: Routes = [
  // 1. Default route now goes to login!
  { path: '', redirectTo: '/login', pathMatch: 'full' },
  { path: 'login', component: AuthComponent }, // <-- ADD THIS ROUTE

  // 2. Main app routes
  { path: 'dashboard', component: DashboardComponent },
  { path: 'live-monitoring', component: LiveMonitoringComponent },
  // ... (keep all your other existing routes here)

  // 3. Wildcard route
  { path: '**', redirectTo: '/login' } 
];
