import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-settings',
  templateUrl: './settings.component.html',
  styleUrls: ['./settings.component.css']
})
export class SettingsComponent implements OnInit {

  activeTab: string = 'thresholds';

  // Mock Settings Data
  settings = {
    thresholds: {
      warningDensity: 70, // Percentage
      criticalDensity: 85, // Percentage
      sustainedTime: 5 // Minutes before alert triggers to avoid false positives
    },
    notifications: {
      emailAlerts: true,
      smsAlerts: false,
      autoDispatch: true
    },
    aiModel: {
      detectionConfidence: 65, // Percentage
      enableThermal: true,
      processingMode: 'Performance' // 'Accuracy' | 'Performance' | 'Balanced'
    }
  };

  constructor() { }

  ngOnInit(): void {
  }

  setTab(tabName: string) {
    this.activeTab = tabName;
  }

  saveSettings() {
    // In a real app, you would call this.apiService.updateSettings(this.settings)
    alert('System configurations updated successfully!');
    console.log('Saved Settings:', this.settings);
  }
}
