import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

  // Top Metrics based on your prototype
  totalCrowdCount: string = '4,281'; //
  avgDensity: string = '68%'; //
  activeAlerts: number = 2; //
  policeDeployed: string = '12 / 12'; //

  // Incident Stream Data
  incidents = [
    {
      id: 1,
      type: 'Density Warning', //
      message: 'Crowd density exceeded safe limits (85% capacity)',
      location: 'STATION GATE A',
      time: '12:14 PM',
      severity: 'high' // Determines border/text color
    },
    {
      id: 2,
      type: 'Entry Alert', //
      message: 'Sudden influx of 200+ people detected.',
      location: 'PLATFORM 4',
      time: '12:02 PM',
      severity: 'medium'
    }
  ];

  constructor() { }

  ngOnInit(): void {
  }

  initiateEmergencyProtocol() {
    // Logic to trigger emergency mode across the system
    const confirmed = confirm('WARNING: Are you sure you want to INITIATE EMERGENCY PROTOCOL? This will dispatch alerts to all deployed guards and local authorities.');
    if (confirmed) {
      console.log('Emergency Protocol Initiated!'); //
      // Call your API service here: this.apiService.triggerEmergency()
    }
  }
}
