import { Component, OnDestroy } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-demo-vid-analysis',
  templateUrl: './demo-vid-analysis.component.html',
  styleUrls: ['./demo-vid-analysis.component.css']
})
export class DemoVidAnalysisComponent implements OnDestroy {

  selectedFile: File | null = null;
  videoUrl: string | null = null; // To play the video on screen

  selectedGate: string = 'Gate 1';
  gates: string[] = ['Gate 1', 'Gate 2', 'Gate 3', 'Gate 4', 'Gate 5'];

  isAnalyzing: boolean = false;
  analysisResult: any = null;

  // Visual & Audio Effects
  liveCount: number = 0;
  showThermal: boolean = false;
  isAlarmRinging: boolean = false;
  alarmInterval: any;

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    if (event.target.files && event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];

      // Convert the file into a playable URL for the <video> tag
      this.videoUrl = URL.createObjectURL(this.selectedFile);

      // Reset everything when a new video is uploaded
      this.analysisResult = null; 
      this.liveCount = 0;
      this.showThermal = false;
      this.stopAlarm();
    }
  }

  uploadAndAnalyze() {
    if (!this.selectedFile) return;

    this.isAnalyzing = true;
    this.analysisResult = null;
    this.liveCount = 0;
    this.showThermal = false;

    const formData = new FormData();
    formData.append('file', this.selectedFile);
    formData.append('gate', this.selectedGate); 

    this.http.post('http://localhost:8000/api/analyze-video', formData).subscribe({
      next: (response: any) => {
        this.isAnalyzing = false;
        this.analysisResult = response;

        // Start the cool "Live Counting" animation
        this.animateCounting(response.crowdCount, response.thresholdExceeded);
      },
      error: (err) => {
        console.error("Analysis Failed", err);
        alert("Error connecting to Python backend!");
        this.isAnalyzing = false;
      }
    });
  }

  // Animates the numbers from 0 to Final Count
  animateCounting(targetCount: number, isCritical: boolean) {
    let current = 0;
    const interval = setInterval(() => {
      current += 2; // Speed of counting
      if (current >= targetCount) {
        this.liveCount = targetCount;
        clearInterval(interval);

        // Trigger Thermal effect and Alarm if crowded!
        if (isCritical) {
          this.showThermal = true;
          this.triggerAudioAlarm(this.selectedGate);
        }
      } else {
        this.liveCount = current;
      }
    }, 30);
  }

  // The Voice Alarm feature!
  triggerAudioAlarm(gateName: string) {
    this.isAlarmRinging = true;

    // The exact message it will speak repeatedly
    const message = `Critical alert! High crowd density detected at ${gateName}. ${gateName}. Security team, please respond.`;

    const speech = new SpeechSynthesisUtterance(message);
    speech.lang = 'en-US';
    speech.rate = 1.0; 
    speech.pitch = 0.9; // Slightly deep robotic voice
    speech.volume = 1.0;

    // Speak instantly
    window.speechSynthesis.speak(speech);

    // Loop the alarm every 8 seconds until stopped
    this.alarmInterval = setInterval(() => {
      window.speechSynthesis.speak(speech);
    }, 8000);
  }

  stopAlarm() {
    this.isAlarmRinging = false;
    clearInterval(this.alarmInterval);
    window.speechSynthesis.cancel(); // Kills the voice
  }

  ngOnDestroy() {
    this.stopAlarm(); // Stop alarm if user leaves the page
  }
}
