@Component({
  selector: 'app-analysis-report',
  templateUrl: './analysis-report.component.html',
  styleUrls: ['./analysis-report.component.css']
})
export class AnalysisReportComponent implements OnInit {

  // Top Metrics Data
  peakDensity: number = 85;
  totalFootfall: string = '24,592';
  incidentsLogged: number = 14;
  avgDwellTime: string = '45 mins';

  // Risk Zone Breakdown Data
  riskZones = [
    { name: 'Main Gate A', percent: 88, level: 'Critical', barColor: 'bg-red-500', textColor: 'text-red-500' },
    { name: 'Platform 4', percent: 75, level: 'High', barColor: 'bg-orange-500', textColor: 'text-orange-500' },
    { name: 'Food Court', percent: 45, level: 'Moderate', barColor: 'bg-yellow-500', textColor: 'text-yellow-500' },
    { name: 'Exit B', percent: 20, level: 'Low', barColor: 'bg-green-500', textColor: 'text-green-500' }
  ];

  constructor() { }

  ngOnInit(): void {
  }

  generateReport() {
    alert("ðŸ“¥ Generating Custom PDF Report... (This will be linked to Python later!)");
  }

}
