import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-analysis-report',
  templateUrl: './analysis-report.component.html',
  styleUrls: ['./analysis-report.component.css']
})
export class AnalysisReportComponent implements OnInit {

  // Summary Metrics
  peakDensity = '85%';
  totalFootfall = '24,592';
  totalIncidents = 14;
  avgDwellTime = '45 mins';

  // Mock data for High-Risk Zones
  riskZones = [
    { name: 'Main Gate A', riskLevel: 88, status: 'Critical' },
    { name: 'Platform 4', riskLevel: 75, status: 'High' },
    { name: 'Food Court', riskLevel: 45, status: 'Moderate' },
    { name: 'Exit B', riskLevel: 20, status: 'Low' }
  ];

  // Mock data for Report History
  reports = [
    { date: 'Oct 24, 2024', type: 'Daily Summary', format: 'PDF', size: '2.4 MB' },
    { date: 'Oct 23, 2024', type: 'Daily Summary', format: 'PDF', size: '2.1 MB' },
    { date: 'Oct 17 - Oct 23', type: 'Weekly Analytics', format: 'CSV', size: '5.8 MB' },
    { date: 'Oct 22, 2024', type: 'Incident Log', format: 'PDF', size: '1.2 MB' }
  ];

  constructor() { }

  ngOnInit(): void {
  }

  downloadReport(reportType: string) {
    alert(`Downloading ${reportType}...`);
    // Logic to trigger file download goes here
  }
}
