import { Component, OnInit } from '@angular/core';
// Make sure to import the interfaces we created earlier!
// import { Guard, SecurityZone } from '../models/guard.model';

@Component({
  selector: 'app-guard-assign',
  templateUrl: './guard-assign.component.html',
  styleUrls: ['./guard-assign.component.css']
})
export class GuardAssignComponent implements OnInit {

  // Mock data for security zones
  zones = [
    { zoneId: 'Z1', zoneName: 'Station Gate A', currentRiskLevel: 'Critical', assignedGuards: ['G001', 'G004'] },
    { zoneId: 'Z2', zoneName: 'Platform 4', currentRiskLevel: 'High', assignedGuards: ['G002'] },
    { zoneId: 'Z3', zoneName: 'Food Court', currentRiskLevel: 'Moderate', assignedGuards: [] },
    { zoneId: 'Z4', zoneName: 'Main Exit B', currentRiskLevel: 'Low', assignedGuards: ['G003'] }
  ];

  // Mock data for security personnel
  guards = [
    { id: 'G001', name: 'Officer Sharma', badgeNumber: 'B-4492', assignedZone: 'Station Gate A', status: 'Responding', contactNumber: '+91 9876543210' },
    { id: 'G002', name: 'Officer Verma', badgeNumber: 'B-1022', assignedZone: 'Platform 4', status: 'Active', contactNumber: '+91 9876543211' },
    { id: 'G003', name: 'Officer Iyer', badgeNumber: 'B-8831', assignedZone: 'Main Exit B', status: 'Active', contactNumber: '+91 9876543212' },
    { id: 'G004', name: 'Officer Singh', badgeNumber: 'B-7745', assignedZone: 'Station Gate A', status: 'Responding', contactNumber: '+91 9876543213' },
    { id: 'G005', name: 'Officer Patel', badgeNumber: 'B-9921', assignedZone: 'Unassigned', status: 'On Break', contactNumber: '+91 9876543214' }
  ];

  constructor() { }

  ngOnInit(): void {
  }

  // Function to simulate dispatching a guard
  dispatchGuard(guardId: string, zoneName: string) {
    const guard = this.guards.find(g => g.id === guardId);
    if (guard) {
      guard.assignedZone = zoneName;
      guard.status = 'Responding';
      alert(`Dispatching ${guard.name} to ${zoneName} immediately.`);
    }
  }
}
