import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';

@Component({
  selector: 'app-guard-list',
  templateUrl: './guard-list.component.html',
  styleUrls: ['./guard-list.component.css']
})
export class GuardListComponent implements OnInit {

  // We use Input so the parent settings component can pass down the data.
  // Defaulting to the mock data from your screenshot.
  @Input() guards: any[] = [
    { id: 1, name: 'Raj Kumar', mobile: '+91 98765 43210', gate: 'Gate 1' },
    { id: 2, name: 'Suresh Selvam', mobile: '+91 99887 76655', gate: 'Gate 2' }
  ];

  @Output() editGuard = new EventEmitter<any>();
  @Output() deleteGuard = new EventEmitter<any>();

  constructor() { }

  ngOnInit(): void {
  }

  onEdit(guard: any) {
    console.log('Editing guard:', guard.name);
    this.editGuard.emit(guard);
  }

  onDelete(guard: any) {
    const confirmDelete = confirm(`Are you sure you want to remove ${guard.name}?`);
    if (confirmDelete) {
      console.log('Deleting guard:', guard.name);
      this.deleteGuard.emit(guard);
    }
  }
}
