import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-guard-register',
  templateUrl: './guard-register.component.html',
  styleUrls: ['./guard-register.component.css']
})
export class GuardRegisterComponent implements OnInit {

  // Guard form data
  guardData = {
    name: '',
    mobile: '',
    gate: 'Gate 1'
  };

  // --- NEW: Multi-tenant Account ID ---
  userEmail: string = '';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    // 1. Get the securely logged-in user's email
    this.userEmail = localStorage.getItem('userEmail') || '';
  }

  registerGuard() {
    if (!this.userEmail) {
      alert("Security Lock: Please Logout and Login again to sync your account.");
      return;
    }

    // 2. Attach the user_email to the payload!
    const payload = {
      name: this.guardData.name,
      mobile: this.guardData.mobile,
      gate: this.guardData.gate,
      user_email: this.userEmail // <-- THIS IS THE MAGIC KEY THAT FIXES THE 422 ERROR!
    };

    this.http.post('http://localhost:8000/api/guards', payload).subscribe({
      next: (res: any) => {
        alert('✅ Guard successfully registered to your account!');
        // Reset the form after success
        this.guardData = { name: '', mobile: '', gate: 'Gate 1' }; 
      },
      error: (err) => {
        console.error(err);
        alert('❌ Failed to save to database.');
      }
    });
  }
}
