import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-guard-assign',
  templateUrl: './guard-assign.component.html',
  styleUrls: ['./guard-assign.component.css']
})
export class GuardAssignComponent implements OnInit {

  // Monitored Zones Data (Matching your screenshot)
  zones = [
    { id: 'Z1', name: 'Station Gate A', risk: 'Critical', deployed: 2, selectedGuard: '' },
    { id: 'Z2', name: 'Platform 4', risk: 'High', deployed: 1, selectedGuard: '' },
    { id: 'Z3', name: 'Food Court', risk: 'Moderate', deployed: 0, selectedGuard: '' },
    { id: 'Z4', name: 'Main Exit B', risk: 'Low', deployed: 1, selectedGuard: '' }
  ];

  // Active Roster Data
  guards = [
    { name: 'Officer Sharma', badge: 'B-4492', status: 'Active' },
    { name: 'Officer Verma', badge: 'B-1022', status: 'On Break' },
    { name: 'Officer Iyer', badge: 'B-8831', status: 'Responding' },
    { name: 'Officer Singh', badge: 'B-7745', status: 'Active' },
    { name: 'Officer Patel', badge: 'B-9921', status: 'Active' }
  ];

  constructor() { }

  ngOnInit(): void {
  }

  // Only show guards who are 'Active' in the dropdown menu
  getAvailableGuards() {
    return this.guards.filter(guard => guard.status === 'Active');
  }

  dispatchGuard(zone: any) {
    if (!zone.selectedGuard) {
      alert('Please select an available unit first!');
      return;
    }

    // Find the guard and update their status
    const guard = this.guards.find(g => g.name === zone.selectedGuard);
    if (guard) {
      guard.status = 'Responding';
      zone.deployed += 1; // Increase unit count in that zone
      alert(`ðŸš¨ Dispatching ${guard.name} to ${zone.name} immediately.`);
    }

    // Reset the dropdown
    zone.selectedGuard = '';
  }

  broadcastMessage() {
    alert("ðŸ“¢ Broadcasting emergency message to all active units...");
  }
}
