import { Component, EventEmitter, Output, OnInit } from '@angular/core';

@Component({
  selector: 'app-guard-register',
  templateUrl: './guard-register.component.html',
  styleUrls: ['./guard-register.component.css']
})
export class GuardRegisterComponent implements OnInit {

  // Form Models
  guardName: string = '';
  mobileNumber: string = '';
  assignedGate: string = '';

  // Generate Gates 1 through 10 dynamically
  gates: string[] = Array.from({ length: 10 }, (_, i) => `Gate ${i + 1}`);

  // Event emitter to send the new guard data up to the parent component
  @Output() guardAdded = new EventEmitter<any>();

  constructor() { }

  ngOnInit(): void {
  }

  registerGuard() {
    // Basic validation
    if (!this.guardName || !this.mobileNumber || !this.assignedGate) {
      alert('Please fill in all fields before registering.');
      return;
    }

    // Create the new guard object
    const newGuard = {
      id: Math.random().toString(36).substr(2, 9), // Simple ID generator for now
      name: this.guardName,
      mobile: this.mobileNumber,
      gate: this.assignedGate
    };

    // Emit the data
    this.guardAdded.emit(newGuard);

    // Optional: Show a success message
    console.log('Guard Registered:', newGuard);

    // Reset the form after successful registration
    this.guardName = '';
    this.mobileNumber = '';
    this.assignedGate = '';
  }
}
