import { Component, OnInit } from '@angular/core';

interface CameraFeed {
  id: string;
  name: string;
  location: string;
  status: 'Live' | 'Offline' | 'Connecting';
  currentDensity: number; // Percentage
  headcount: number;
}

@Component({
  selector: 'app-live-monitoring',
  templateUrl: './live-monitoring.component.html',
  styleUrls: ['./live-monitoring.component.css']
})
export class LiveMonitoringComponent implements OnInit {

  // AI Overlay Toggles
  showBoundingBoxes: boolean = true;
  showHeatmap: boolean = false;

  // Mock Camera Data
  cameras: CameraFeed[] = [
    { id: 'CAM-01', name: 'Main Entrance - Alpha', location: 'Gate A', status: 'Live', currentDensity: 45, headcount: 112 },
    { id: 'CAM-02', name: 'Platform 4 - North', location: 'Platform 4', status: 'Live', currentDensity: 82, headcount: 340 },
    { id: 'CAM-03', name: 'Food Court - Central', location: 'Hall B', status: 'Live', currentDensity: 60, headcount: 205 },
    { id: 'CAM-04', name: 'Exit B - Escalators', location: 'Gate B', status: 'Offline', currentDensity: 0, headcount: 0 }
  ];

  activeCamera: CameraFeed = this.cameras[0];

  constructor() { }

  ngOnInit(): void {
  }

  selectCamera(cam: CameraFeed) {
    if (cam.status === 'Live') {
      this.activeCamera = cam;
    }
  }

  toggleBoundingBoxes() {
    this.showBoundingBoxes = !this.showBoundingBoxes;
    if (this.showBoundingBoxes) this.showHeatmap = false; // Usually mutually exclusive in simple UIs
  }

  toggleHeatmap() {
    this.showHeatmap = !this.showHeatmap;
    if (this.showHeatmap) this.showBoundingBoxes = false;
  }
}
