import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-camera-setup',
  templateUrl: './camera-setup.component.html',
  styleUrls: ['./camera-setup.component.css']
})
export class CameraSetupComponent implements OnInit {

  // Setup the 5 default empty nodes
  cameraNodes = [
    { gate: 'Gate 1', rtsp_url: '', status: 'Offline', isSaving: false },
    { gate: 'Gate 2', rtsp_url: '', status: 'Offline', isSaving: false },
    { gate: 'Gate 3', rtsp_url: '', status: 'Offline', isSaving: false },
    { gate: 'Gate 4', rtsp_url: '', status: 'Offline', isSaving: false },
    { gate: 'Gate 5', rtsp_url: '', status: 'Offline', isSaving: false }
  ];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.loadSavedCameras();
  }

  // Fetch from MongoDB when page loads
  loadSavedCameras() {
    this.http.get<any[]>('http://localhost:8000/api/cameras').subscribe({
      next: (savedCams) => {
        // Match saved DB data with our UI grid
        savedCams.forEach(saved => {
          const node = this.cameraNodes.find(n => n.gate === saved.gate);
          if (node) {
            node.rtsp_url = saved.rtsp_url;
            node.status = saved.rtsp_url ? 'Live' : 'Offline'; // Simple check for now
          }
        });
      },
      error: (err) => console.error("Could not load cameras", err)
    });
  }

  saveCamera(cam: any) {
    cam.isSaving = true;
    
    const payload = {
      gate: cam.gate,
      rtsp_url: cam.rtsp_url,
      status: cam.rtsp_url ? 'Live' : 'Offline'
    };

    this.http.post('http://localhost:8000/api/cameras', payload).subscribe({
      next: (res: any) => {
        cam.isSaving = false;
        cam.status = payload.status;
        alert(`‚úÖ Configuration saved successfully for ${cam.gate}!`);
      },
      error: (err) => {
        cam.isSaving = false;
        alert("‚ùå Failed to save to database. Is Python running?");
      }
    });
  }

  testConnection(cam: any) {
    if (!cam.rtsp_url) {
      alert("Please enter an RTSP URL first.");
      return;
    }
    alert(`üì° Pinging ${cam.rtsp_url}... \n(For hackathon demo, we will verify this directly in the Live Video tab)`);
  }
}
